import {React, useState} from 'react';

export default props => {
    const [value, setValue] = useState('');

    function setValueInput(e) {
        setValue(e.target.value);
        console.log(props);
        props.enviarDados(value);
    }

    let className = 'input';
    if (props.style) {
        className+=' ml-1';
    }

    return (
        <>
            <input className={className} value={value} onChange={setValueInput} placeholder={props.placeholder}/>
        </>
    )
}
